    private void btnConfirmActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnConfirmActionPerformed
        //Getting user information
        String userCode = tfCode.getText();
        String Errors = "";
        //Validation
        if (userCode.length() != 4) {
            Errors += "The code should only contain 4 digits <br>";
        }

        try {
            int userNumCode = Integer.parseInt(userCode);
            //Checking if the user's code matches generated code
            if (userNumCode == SignUpGUI.code) {
                //Opening main menu and closing current form
                new MainMenuGUI().setVisible(true);
                this.setVisible(false);
                addToDB(SignUpGUI.Username, SignUpGUI.password);
            } else {
                Errors += "The code you entered did not match the security"
                        + " code. Check your email and try again.";
            }
        } catch (NumberFormatException nfe) {
            Errors += "The code should only contain numerical values <br>";
        } catch (SQLException ex) {
            Logger.getLogger(ConfirmSignUpGUI.class.getName()).log(Level.SEVERE, null, ex);
        }
    
    //Displaying errors to the user
    lblInfo.setText ("<html>" + Errors + "</html>");

    }//GEN-LAST:event_btnConfirmActionPerformed

