    private void addToDB(String Username, String password) throws SQLException {
        DatabaseConnect db = new DatabaseConnect();
        String sQry = "INSERT INTO tblUser (Username, Password) VALUES('" 
                + SignUpGUI.Username + "', '" + SignUpGUI.password + "')";
        
        try {
            db.runInsert(sQry);
        } catch (SQLException ex) {
            Logger.getLogger(SignUpGUI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

